<?php/** * Created by PhpStorm. * User: LK * Date: 2019/11/6 * Time: 18:10 */namespace App\Models;use Illuminate\Database\Eloquent\Model;use Illuminate\Support\Facades\DB;class Imsi extends Model {    protected  $table = 'imsi';    public $timestamps = false;    /**获取自己的码数量     * @param $user_id     * @return int     */    public static function getprinum($user_id){        $imsinum = Imsi::where(array('user_id'=>$user_id))->count();        return (int)$imsinum;    }}