<?php/** * Created by PhpStorm. * User: LK * Date: 2019/11/3 * Time: 9:14 */namespace App\Models;use Illuminate\Database\Eloquent\Model;use Illuminate\Support\Facades\DB;class Userscount extends Model {    protected  $table = 'users_count';    public $timestamps = false;    /**码商账户数据     * @param $user_id     * @return int     */    public static function gettolscore($user_id) {        return Userscount::where(array('user_id'=>$user_id))->first();    }}